const base   = ($vars.SB_URL_BASE || '').replace(/\/$/, '');
const bucket = $vars.SB_BUCKET || 'images';
const prefix = ($vars.SB_PREFIX || 'ig/uploads').replace(/^\/+|\/+$/g, '');
const items  = $input.all();

const urls = items.map((it, i) => {
  const fileName = it.json?.fileName || it.json?.filename || it.json?.name;
  if (!fileName) throw new Error(`Missing fileName for item ${i+1}`);
  return `${base}/storage/v1/object/public/${bucket}/${prefix}/${fileName}`;
});

return [{ json: { image_url: urls } }];

const raw = $json.image_url ?? [];
let urls = Array.isArray(raw) ? raw : [raw];
urls = urls.filter(u => typeof u === "string" && /^https?:\/\//.test(u));
// remove duplicates
urls = [...new Set(urls)];
if (urls.length > 5) urls = urls.slice(0, 5);
return urls.map((u, i) => ({ json: { index: i+1, image_url: u, post_type: urls.length>=2?'carousel':'single' } }));

// Make timestamped names like image_2025-10-14-17-32-05_1.jpg
const items = $input.all();
const folder = ($vars.SB_PREFIX || 'ig/upload').replace(/^\/+|\/+$/g, ''); // path inside bucket
const extDefault = 'jpg';

// timestamp in LOCAL server time; set SB_TZ if you want a specific time zone
const tz = $vars.SB_TZ; // e.g. 'Europe/Istanbul'
function nowParts() {
  const d = tz ? new Date(new Date().toLocaleString('en-US', { timeZone: tz })) : new Date();
  const p = (n)=>String(n).padStart(2,'0');
  return {
    y: d.getFullYear(),
    M: p(d.getMonth()+1),
    d: p(d.getDate()),
    h: p(d.getHours()),
    m: p(d.getMinutes()),
    s: p(d.getSeconds()),
  };
}

const { y,M,d,h,m,s } = nowParts();
const ts = `${y}-${M}-${d}-${h}-${m}-${s}`;

return items.map((it, i) => {
  const ext =
    it.json.fileExtension ||
    it.binary?.data?.fileExtension ||
    it.binary?.data?.fileext ||
    extDefault;

  const cleanExt = String(ext).replace(/^\./,'').toLowerCase();
  const fileName = `image_${ts}_${i+1}.${cleanExt}`;
  const key = `${folder}/${fileName}`;

  return { json: { fileName, key }, binary: it.binary };
});

// Get all input items from Merge2 (should contain text + id)
const items = $input.all();

// Gather and deduplicate child media IDs
let ids = items.map(i => i.json.id).filter(Boolean);
ids = [...new Set(ids)]; // remove duplicates

// Validate: require at least 2 child items for a carousel
if (ids.length < 2) {
  throw new Error(`Expected at least 2 child IDs, found ${ids.length}`);
}

// Get caption from the first merged item or fallback variable
const caption =
  (items[0]?.json?.text ?? $vars.IG_DEFAULT_CAPTION ?? '').toString().trim();

// Return formatted output for Create Carousel Container
return [
  {
    json: {
      child_ids: ids,                     // array form
      child_ids_csv: ids.join(','),       // string form for API call
      caption                             // caption text
    }
  }
];

const items = $input.all();
const resizeBase = "https://wsrv.nl/?url=";
const endCardUrl = $vars.ENDCARD_URL;

// Collect URLs from either AI (already resized via Weserv) or Link scraping
const allUrls = items.flatMap(item => {
  // From AI generation (already formatted)
  if (item.json.image_url && Array.isArray(item.json.image_url)) {
    return item.json.image_url;
  }
  // From link scraping (needs formatting)
  if (item.json.link) {
    return [item.json.link];
  }
  return [];
}).filter(url => url && /^https?:\/\//i.test(url));

// Map to output format
const resizedImages = allUrls.map(url => {
  // Skip resizing if already processed by Weserv
  if (url.includes('weserv.nl') || url.includes('wsrv.nl')) {
    return { json: { image_url: url } };
  }
  // Apply resizing for raw URLs
  const resizedUrl = `${resizeBase}${encodeURIComponent(url)}&w=1080&h=1350&fit=cover&output=jpg&q=90`;
  return { json: { image_url: resizedUrl } };
});

// Add end card if fewer than 2 images
if (resizedImages.length < 2 && endCardUrl) {
  resizedImages.push({ json: { image_url: endCardUrl } });
}

return resizedImages;

const items = $input.all();

const normalizeUrl = (url) => {
  url = url.trim();
  url = url.replace(/[^\x20-\x7E]/g, ""); // remove non-printable ascii characters
  if (!/^https?:\/\//i.test(url)) {
    if (/^\/\//.test(url)) {
      url = "https:" + url;
    } else if (!/^[a-z][a-z0-9+.-]*:/i.test(url)) {
      url = "https://" + url;
    }
  }
  if (/^https?:\/\/[^/]/i.test(url)) {
    url = encodeURI(decodeURI(url));
  }
  return url;
};

const normalizedUrls = items.map((item) => {
  const url = item?.json?.SourceLinks;
  if (url) {
    return { json: { url: normalizeUrl(url) } };
  }
});

return normalizedUrls;

const items = $input.all();
const updatedItems = items.map((item) => {
