const creationId = $json.id;                 // must be the CONTAINER id from "Create Carousel Container"
const token = $vars.IG_ACCESS_TOKEN;

function sleep(ms){ return new Promise(r => setTimeout(r, ms)); }

async function poll() {
  const intervalMs = 3000;   // 3s
  const maxTries   = 30;     // ~90s total

  for (let i = 0; i < maxTries; i++) {
    const res = await this.helpers.httpRequest({
      method: 'GET',
      url: `https://graph.facebook.com/v24.0/${creationId}`,
      qs: { fields: 'status_code', access_token: token },
      json: true
    });

    const sc = res?.status_code;
    console.log('container poll:', { sc });

    if (sc === 'FINISHED') return res;
    if (sc === 'ERROR') throw new Error(`Container returned ERROR: ${JSON.stringify(res)}`);

    await sleep(intervalMs);
  }
  throw new Error('Container not ready after polling window.');
}

const result = await poll.call(this);
return [{ json: { ...$json, ...result } }];

/**
 * Build AI Images (OpenAI) â€“ v3
 * Generates up to 5 unique images from a single prompt.
 */

const CFG = {
  model: "dall-e-3",
  size: "1024x1536",
  quality: "high",
  format: "png",
  background: "transparent",
  variants: 5,
  retries: 2
};

const sleep = (ms) => new Promise(r => setTimeout(r, ms));

async function gen(prompt) {
  let lastErr;
  for (let i = 0; i <= CFG.retries; i++) {
    try {
      const r = await this.helpers.httpRequest({
        method: "POST",
        url: "https://api.openai.com/v1/images/generations",
        headers: {
          "Authorization": `Bearer ${$vars.OPENAI_API_KEY}`,
          "OpenAI-Project": $vars.OPENAI_PROJECT,
          "Content-Type": "application/json"
        },
        body: {
          model: CFG.model,
          prompt,
          size: CFG.size,
          quality: CFG.quality,
          output_format: CFG.format,
          background: CFG.background
        },
        json: true
      });
      const url = r?.data?.[0]?.url;
      if (!url) throw new Error("No URL returned");
      return url;
    } catch (e) {
      lastErr = e;
      if (i < CFG.retries) await sleep(800 * (i + 1));
    }
  }
  const msg = lastErr?.response?.body?.error?.message || lastErr?.message || "Unknown error";
  throw new Error(msg);
}

try {
  const base = String($json?.ImagePrompt || "").trim();
  if (!base)
    return { ...$json, status: "error", node: "Build AI Images (OpenAI) â€“ v3", message: "Missing ImagePrompt" };

  const n = Math.min(Math.max(Number($json?.Variants) || CFG.variants, 1), 5);
  const urls = [];

  // Make multiple calls for subtle variations
  for (let i = 0; i < n; i++) {
    const p = `${base} (different perspective ${i + 1})`;
    urls.push(await gen.call(this, p));
    await sleep(200);
  }

  return {
    ...$json,
    generator: "openai",
    model: CFG.model,
    size: CFG.size,
    quality: CFG.quality,
    format: CFG.format,
    background: CFG.background,
    variants: urls.length,
    GeneratedImageURLs: urls,
    GeneratedImageURL: urls[0] || null,
    status: "ok"
  };

} catch (err) {
  return { ...$json, status: "error", node: "Build AI Images (OpenAI) â€“ v3", message: err.message };
}

/**
