function toWeserv(u) {
  if (!u) return null;
  const noProto = u.replace(/^https?:\/\//, "");
  const encoded = encodeURIComponent(noProto);
  return `https://images.weserv.nl/?url=${encoded}&w=1080&h=1350&fit=cover&we&il&output=jpg`;
}

try {
  const urls = Array.isArray($json.GeneratedImageURLs)
    ? $json.GeneratedImageURLs
    : ($json.GeneratedImageURL ? [$json.GeneratedImageURL] : []);

  const out = urls.map(toWeserv).filter(Boolean);

  return {
    ...$json,
    image_url: out,  // âœ… ADDED - Standard format for downstream
    WeservURLs: out,
    WeservURLFirst: out[0] || null,
    WeservInfo: "1080Ã—1350 cover-cropped via images.weserv.nl"
  };
} catch (e) {
  return { ...$json, status: "error", node: "Make 1080x1350 Weserv URLs", message: e.message };
}

// Extract .jpg/.jpeg/.webp/.avif links from large HTML safely (chunked) and emit {json:{link}}.

const htmlRaw = String($json.body || $json.data || $json.html || '');

const MAX_CHARS = 1_500_000;
const CHUNK = 200_000;
const MAX_URLS_PER_PAGE = 1000;

const allow = /https?:\/\/[^\s"'()<>]+?\.(?:jpe?g|webp|avif)(?:\?[^\s"'<>]*)?/gi;

const html = htmlRaw.length > MAX_CHARS ? htmlRaw.slice(0, MAX_CHARS) : htmlRaw;

const set = new Set();
for (let i = 0; i < html.length && set.size < MAX_URLS_PER_PAGE; i += CHUNK) {
  const slice = html.slice(i, i + CHUNK);
  for (const m of slice.matchAll(allow)) {
    if (m && m[0]) set.add(m[0]);
    if (set.size >= MAX_URLS_PER_PAGE) break;
  }
}

let out = [...set].map(u => ({ json: { link: String(u) } }));
return out.slice(0, 1000).map(it => (it && typeof it.json === 'object' ? it : { json: it }));

/**
 * VISUAL FILTER + DEDUP (Lenient + URL heuristics + fallback)
 * Use this in an n8n Code node after image download (with binary)
 */

const items = $input.all();

// ---------- CONFIGURATION ----------
const MIN_W = 600;
const MIN_H = 800;
const MIN_RATIO = 1.05;
const FALLBACK_MIN_BYTES = 100 * 1024; // 100 KB
const ALLOWED_MIME = /^(image\/jpeg|image\/jpg|image\/webp|image\/avif)$/i;
const MAX_OUTPUT = 120;
const PASSTHRU_TOP_N = 30;
//------------------------------------

const crypto = require('crypto');

function getLink(it) {
  return it?.json?.link || it?.json?.url || '';
}
function getMime(it) {
  const m = it?.binary?.data?.mimeType || it?.json?.headers?.['content-type'] || '';
  return String(m).toLowerCase();
}
function getSizeBytes(it) {
  const binSz = Number(it?.binary?.data?.fileSize || 0);
  if (binSz) return binSz;
  const hLen = Number(it?.json?.headers?.['content-length'] || 0);
  return hLen || 0;
}
function getBuf(it) {
  const b64 = it?.binary?.data?.data;
  if (!b64) return null;
  try { return Buffer.from(b64, 'base64'); } catch { return null; }
}

// ---------- JPEG ----------
function sizeJPEG(buf) {
  if (!buf || buf.length < 4 || buf.readUInt16BE(0) !== 0xFFD8) return null;
  let i = 2;
  while (i + 1 < buf.length) {
    while (i < buf.length && buf[i] !== 0xFF) i++;
    while (i < buf.length && buf[i] === 0xFF) i++;
    const marker = buf[i]; i++;
    if ((marker >= 0xD0 && marker <= 0xD7) || marker === 0x01) continue;
    if (i + 1 >= buf.length) break;
    const segLen = buf.readUInt16BE(i);
    const segStart = i + 2;
    const segEnd = segStart + (segLen - 2);
    if (segEnd > buf.length) break;
    const isSOF = (marker >= 0xC0 && marker <= 0xC3);
    if (isSOF) {
      const h = buf.readUInt16BE(segStart + 1);
      const w = buf.readUInt16BE(segStart + 3);
      if (w > 0 && h > 0) return { w, h, type: 'jpeg' };
    }
    i = segEnd;
  }
  return null;
}

// ---------- WEBP ----------
function sizeWEBP(buf) {
  if (!buf || buf.length < 30) return null;
  if (buf.toString('ascii',0,4) !== 'RIFF' || buf.toString('ascii',8,12) !== 'WEBP') return null;
  const w = 1 + buf.readUIntLE(24, 3);
  const h = 1 + buf.readUIntLE(27, 3);
  return (w && h) ? { w, h, type: 'webp' } : null;
}

// ---------- AVIF ----------
function sizeAVIF(buf) {
  let off = 0;
  while (off + 8 <= buf.length) {
    const size = buf.readUInt32BE(off);
    const type = buf.toString('ascii', off + 4, off + 8);
    if (!size || size < 8) break;
    if (type === 'ispe' && off + 20 <= buf.length) {
      const w = buf.readUInt32BE(off + 12);
      const h = buf.readUInt32BE(off + 16);
      if (w && h) return { w, h, type: 'avif' };
    }
    off += size;
  }
  return null;
}

function getSizeFromBytes(link, buf) {
  const l = link.toLowerCase();
  if (l.includes('.jpg') || l.includes('.jpeg')) return sizeJPEG(buf);
  if (l.includes('.webp')) return sizeWEBP(buf);
  if (l.includes('.avif')) return sizeAVIF(buf);
  if (buf.readUInt16BE(0) === 0xFFD8) return sizeJPEG(buf);
  if (buf.toString('ascii',0,4) === 'RIFF') return sizeWEBP(buf);
  return null;
}

function getSizeFromUrl(u) {
  if (!u) return null;
  let m = u.match(/_CR\d+,\d+,(\d+),(\d+)_/i);
  if (m) return { w: +m[1], h: +m[2], type: 'url' };
  m = u.match(/_UX(\d+)_/i); if (m) return { w: +m[1], h: Math.round(+m[1]*1.5), type: 'url_guess' };
  m = u.match(/_UY(\d+)_/i); if (m) return { h: +m[1], w: Math.round(+m[1]/1.5), type: 'url_guess' };
  m = u.match(/(\d{3,5})x(\d{3,5})/); if (m) return { w: +m[1], h: +m[2], type: 'url' };
  m = u.match(/[?&](?:w|width)=(\d{3,5})/i); const wq = m ? +m[1] : null;
  m = u.match(/[?&](?:h|height)=(\d{3,5})/i); const hq = m ? +m[1] : null;
  if (wq && hq) return { w: wq, h: hq, type: 'url' };
  return null;
}

function urlCore(u) {
  return String(u || '').replace(/[?#].*$/, '').replace(/_[A-Z]{1,3}\d{2,5}[^.]*\./, '.');
}

const seenHashes = new Set();
const seenUrlCore = new Set();
const passed = [];
const scoredForPassthru = [];

for (const it of items) {
  const link = getLink(it);
  if (!link) continue;
  const mime = getMime(it);
  if (mime && !ALLOWED_MIME.test(mime)) continue;

  const buf = getBuf(it);
  const sizeBytes = getSizeBytes(it);
  scoredForPassthru.push({ it, link, sizeBytes });

  if (buf?.length) {
    const h = crypto.createHash('sha256').update(buf).digest('hex');
    if (seenHashes.has(h)) continue;
    seenHashes.add(h);
  } else {
    const k = urlCore(link);
    if (seenUrlCore.has(k)) continue;
    seenUrlCore.add(k);
  }

  let dims = buf ? getSizeFromBytes(link, buf) : null;
  if (!dims) dims = getSizeFromUrl(link);

  let keep = false;
  if (dims) {
    const { w, h } = dims;
    const ratio = h / w;
    keep = (w >= MIN_W && h >= MIN_H && ratio >= MIN_RATIO);
  } else {
    keep = (sizeBytes >= FALLBACK_MIN_BYTES);
  }

  if (keep) {
    passed.push({
      json: {
        link,
        width: dims?.w ?? null,
        height: dims?.h ?? null,
        ratio: dims ? (dims.h / dims.w) : null,
        bytes: buf?.length || sizeBytes || null,
        mimeType: mime || null,
      },
      binary: it.binary ?? undefined,
    });
    if (passed.length >= MAX_OUTPUT) break;
  }
}

if (!passed.length) {
  const sorted = scoredForPassthru.sort((a,b)=> (b.sizeBytes||0) - (a.sizeBytes||0)).slice(0, PASSTHRU_TOP_N);
  const out = [];
  const localSeen = new Set();
  for (const s of sorted) {
    const k = urlCore(s.link);
    if (localSeen.has(k)) continue;
    localSeen.add(k);
    out.push({
      json: { link: s.link, bytes: s.sizeBytes || null, reason: 'passthrough_largest' },
      binary: s.it.binary ?? undefined,
    });
  }
  return out.length ? out : [{ json: { error: 'No images passed and nothing to passthrough.' } }];
}

return passed;
